package exp;

public class Ex12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		switch (i) {
		case 0:
			System.out.println("zero");
			break;
		case 1:
			System.out.println("one");
		case 2:
			System.out.println("two");
		default:
			System.out.println("default");
		}
		// no break fall until default
	}

}
